import React, { useEffect, useState } from 'react';
import { fetchWeatherData } from './services/weatherService';

const App = () => {
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState(null);
    const [city, setCity] = useState(''); // To store the input city
    const [country, setCountry] = useState(''); // To store the input country

    const getWeatherData = async () => {
        try {
            const data = await fetchWeatherData(city, country); // Pass city and country to the service
            if (data) {
                setWeatherData(data);
            }
        } catch (err) {
            setError(err.message);
        }
    };

    useEffect(() => {
        if (city && country) {
            getWeatherData();
        }
    }, [city, country]);

    return (
        <div>
            <h1>Weather Monitoring System</h1>
            <input
                type="text"
                placeholder="Enter city"
                value={city}
                onChange={(e) => setCity(e.target.value)}
            />
            <input
                type="text"
                placeholder="Enter country"
                value={country}
                onChange={(e) => setCountry(e.target.value)}
            />
            <button onClick={getWeatherData}>Get Weather</button>

            {error && <p>{error}</p>}
            {weatherData && (
                <div>
                    <h2>Current Weather in {city}</h2>
                    {weatherData.main ? (
                        <>
                            <p>Main Condition: {weatherData.weather[0]?.main || 'N/A'}</p>
                            <p>Current Temperature: {weatherData.main.temp} °C</p>
                            <p>Feels Like: {weatherData.main.feels_like} °C</p>
                        </>
                    ) : (
                        <p>No weather data available.</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default App;
